import React, { useEffect, useState } from "react";
import { fetchSearchResults } from "../../service";
import { Link } from "react-router-dom";

export function SearchResults({ searchTerm }) {
  const [results, setResults] = useState([]);

  useEffect(() => {
    const fetchAPI = async () => {
      setResults(await fetchSearchResults(searchTerm));
    };

    fetchAPI();
  }, [searchTerm]);

  return (
    <div className="container">
      <div className="row">
        <div className="col">
          <h2>Search Results for "{searchTerm}"</h2>
          {results.length === 0 ? (
            <p>No results found.</p>
          ) : (
            <ul>
              {results.map((result) => (
                <li key={result.id}>
                  <Link to={`/movie/${result.id}`}>{result.title}</Link>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}
