// searchReducer.js
import { setSearchResults, updateSearchResults } from '../actions/searchActions';

const initialState = {
  results: [],
};

const searchReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_SEARCH_RESULTS:
      return {
        ...state,
        results: action.payload,
      };
    case UPDATE_SEARCH_RESULTS:
      // Handle updating search results if needed
      return state;
    default:
      return state;
  }
};

export default searchReducer;
