import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { fetchSearchResults } from "../../service";
import { useDispatch, useSelector } from 'react-redux';
import { setSearchResults } from "../../actions/searchActions";

export function SearchPage() {
  const [searchTerm, setSearchTerm] = useState("");
  const history = useHistory();
  const dispatch = useDispatch();
  const searchResults = useSelector(state => state.search.results);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearch = async (event) => {
    event.preventDefault();
    if (searchTerm.trim() !== '') {
      const results = await fetchSearchResults(searchTerm);
      dispatch(setSearchResults(results)); // Update the search results in the Redux store
      history.push(`/search/${searchTerm}`);
      setSearchTerm('');
    }
  };

  useEffect(() => {
    return () => {
      // Cleanup function to cancel any pending asynchronous tasks
      // Cancel any ongoing network requests or subscriptions here
    };
  }, []);

  return (
    <div>
      <form className="form-inline my-2 my-lg-0" onSubmit={handleSearch}>
        <input
          className="form-control mr-sm-2"
          type="search"
          placeholder="Search"
          aria-label="Search"
          value={searchTerm}
          onChange={handleInputChange}
        />
        <button className="btn btn-outline-light my-2 my-sm-0" type="submit">
          Search
        </button>
      </form>

      <div>
        {searchResults && searchResults.length > 0 ? (
          searchResults.map((result) => (
            <div key={result.id}>
              <h3>{result.title}</h3>
              <p>{result.description}</p>
              {/* Render other details of the search result as needed */}
            </div>
          ))
        ) : (
          <p>No search results found.</p>
        )}
      </div>
    </div>
  );
}
